<?phprequire_once("../Modelo/autoload.php");if($_POST) {    $email = $_POST['email'] ?? '';    $pass = $_POST['password'] ?? '';    $response = [];    if (empty($email) || empty($pass)) {        $response = array("type_msg" => "info", "msg" => "Debe llenar todos los campos", "title" => "Atenci칩n");    } else {        $objLogin = new Login();        $objLogin = $objLogin->sys_login($email, $pass);        if ($objLogin == 0) {            $response = array("type_msg" => "info", "msg" => "El usuario ingresado no existe en el sistema", "title" => "Atenci칩n");            echo json_encode($response);        } elseif ($objLogin == 1) {            session_start();            $_SESSION['user'] = $email;            echo 1;        } elseif ($objLogin == 2) {            $response = array("type_msg" => "warning", "msg" => "Usuario y/o contrase침a incorrecta", "title" => "Atenci칩n");            echo json_encode($response);        }    }}?>