<?phprequire_once "autoload.php";class Login extends Conexion{    private $strEmail ;    private $strPassword;    private $objConexion;  public function __construct()  {        $this->objConexion = new Conexion();        $this->objConexion = $this->objConexion->system_connect();  }  public function sys_login(string $email, string $pass)  {    $this->strEmail = $email;    $this->strPassword = $pass;    $sql =  "SELECT id, user_name,pass FROM t_users WHERE email = '$this->strEmail'";    $select = $this->objConexion->query($sql);    $response = $select->fetch(PDO::PARAM_STR);    if(is_array($response) and count($response))    {        $responseQuery = password_verify($this->strPassword, $response['pass']);       if($responseQuery)       {           $response['responseQuery'] = 1;           return $response;       }else{           $response['responseQuery'] = 2;           return $response;       }    }else{        return 0;    }  }}