<?phprequire_once "autoload.php";class Login extends Conexion{    private $strEmail ;    private $strPassword;    private $objConexion;  public function __construct()  {        $this->objConexion = new Conexion();        $this->objConexion = $this->objConexion->system_connect();  }  public function sys_login(string $email, string $pass)  {    $this->strEmail = $email;    $this->strPassword = $pass;    $sql =  "SELECT pass FROM t_users WHERE email = '$this->strEmail'";    $select = $this->objConexion->query($sql);    $response = $select->fetch(PDO::PARAM_STR);   /* echo "<pre>";    print_r($response);      echo "</pre>";      exit();*/    if(is_array($response) and count($response))    {        $response = password_verify($this->strPassword, $response['pass']);       if($response)       {           return 1;       }else{           return 2;       }    }else{        return 0;    }  }}