<?phpclass Usuario extends Conexion{    private $strFullName;    private $strEmail;    private $strPassword;    private $objConexion;    public function __construct()    {        $this->objConexion = new Conexion();        $this->objConexion = $this->objConexion->system_connect();    }    public function add_user(array $user_data)    {        $this->strFullName = $user_data["cliente"];        $this->strEmail = $user_data["email"];        $this->strPassword = $user_data["password"];        $this->strPassword = password_hash($this->strPassword, PASSWORD_BCRYPT);        if($this->strFullName == "" || $this->strEmail == "" || $this->strPassword == "")        {            $id_insert = 0;        }else{            $sql = "INSERT INTO t_users VALUES(null,?,?,?,now())";            $insert = $this->objConexion->prepare($sql);            $user_data = array($this->strFullName, $this->strEmail, $this->strPassword);            $res_insert = $insert->execute($user_data);            $id_insert = $this->objConexion->lastInsertId();        }        return $id_insert;    }    public function get_users()    {        $sql = "SELECT  user_name, email, register_date FROM t_users";        $select = $this->objConexion->query($sql);        return $select->fetchAll(PDO::FETCH_ASSOC);    }}